using Soneta.Types;
using Soneta.Business;
using Soneta.Business.UI.DxReports;

namespace Geeks {

	public class CustomReportSnippet : ReportSnippet {

		public class CustomParams : ContextBase {

			public CustomParams(Context context) : base(context) {}

			[Caption("Dodatkowy parametr")]
			public string CustomProperty { get; set; }

		}

	}

}

using Soneta.Types;
using Soneta.Business;
using Soneta.Business.UI.DxReports;

using DevExpress.XtraReports.UI;

namespace Geeks {

public class ExtraSnippet : CustomReportSnippet {

[Context]
public CustomParams Parameters { get; set; }

[DxBind]
public void CustomCalc_GetValue(object sender, GetValueEventArgs e)
=> e.Value = string.IsNullOrEmpty(Parameters.CustomProperty) ? null : $"Notatka: {Parameters.CustomProperty}";

}

}
